INSERT INTO public.exercise(code, title, subtitle, image_url_list, muscle_image_url, video_url, main_muscles, auxiliary_muscles, stabilizers, execution, remarks) VALUES (
  'BIC12',
  'Uginanie przedramion podchwytem',
  'na wyciągu drążkiem / klamrą',
  '/images/uginanie_podchwytem_wyciag_1.jpg;/images/uginanie_podchwytem_wyciag_2.jpg',
  '/images/biceps_2.png',
  'https://www.youtube.com/watch?v=nIW7sQ9BADM',
  'dwugłowy ramienia',
  'ramienny, ramienno-promieniowy',
  'naramienny akton przedni,prostowniki grzbietu, czworoboczny część górna i środkowa, dźwigacz łopatki, zginacze przedramion ',
  'Stojąc, złap podchwytem drążek połączony z wyciągiem dolnym, na szerokość barków (więcej w uwagach). Wypnij klatkę piersiową i weź wdech. Przez cały czas trzymając łokcie jak najbliżej ciała zacznij zginanie przedramion. W momencie maksymalnego napięcia mięśni zrób wydech i powoli kontrolując ciężar powróć do pozycji wyjściowej, czyli do momentu maksymalnego rozciągnięcia mięśni, jednocześnie robiąc wdech.',
  'Nadgarstki powinny być zablokowane w taki sposób, aby się dodatkowo nie zginały. Dzięki temu zginacze nie wykonują większej pracy. Ciężaru nie należy opuszczać, aż do pełnego wyprostowania łokci, co zapobiega rozluźnieniu mięśnia dwugłowego oraz zabezpieczy stawy. Można użyć gryfu łamanego, co jest szczególnie preferowane przez osoby odczuwające napięcia w nadgarstkach stosując gryf prosty. Ćwiczenie to można wykonać również w oparciu o ławkę skośną bądź też jednorącz przy pomocy pojedynczej rączki wyciągu.\r\n\r\nSzerokość chwytu: Uchwyt na szerokość barków angażuje równomiernie obie głowy mięśnia dwugłowego. Uchwyt szerszy angażuje bardziej głowę krótką , uchwyt węższy bardziej głowę długą.'
);

INSERT INTO public.exercise(code, title, subtitle, image_url_list, muscle_image_url, video_url, main_muscles, auxiliary_muscles, stabilizers, execution, remarks) VALUES (
  'BIC13',
